sys_script_include.script:
var processExcelToMRVS = Class.create();
processExcelToMRVS.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {

    //Returns booking template when room parameter set
    parseExcel: function() {
		gs.info('running parseExcel');
        var attachSysid = this.getParameter('sysparm_attachment') + '';
        var grAttachment = new GlideRecord('sys_attachment');
        grAttachment.addQuery('sys_id', attachSysid);
        grAttachment.query();

        if (grAttachment.next()) {
			gs.info('found attachment');
			var results = [];
            var attachmentSysId = grAttachment.sys_id;
            var attachment = new GlideSysAttachment();
            var attachmentStream = attachment.getContentStream(attachmentSysId);

            var excelParser = new sn_impex.GlideExcelParser();
            excelParser.parse(attachmentStream);

            while (excelParser.next()) {
				gs.info('processing excel rows');
                var row = excelParser.getRow();
				var rowValues = {
					'asset_num': row.getCell(0).getValue(),
					'serial_num': row.getCell(1).getValue(),
					'description': row.getCell(2).getValue()
				}
				results.push(rowValues);
			}
			return JSON.stringify(results);
        }
	},

    type: 'processExcelToMRVS'
});
